"use strict";

function showBackFrame() {
  $(".back_frame").addClass("active");
}
function closeBackFrame() {
  $(".back_frame").removeClass("active"), $("#mobile-menu").removeClass("active"), $("body").removeClass("lock");
}
function bodyLock() {
  $("body").addClass("lock");
}
$(document).ready(function () {
  $("#open_mobile_menu").on("click", function (e) {
    e.preventDefault(), $("#mobile-menu").addClass("active"), bodyLock(), showBackFrame();
  }), $(".back_frame").on("click", function (e) {
    e.preventDefault(), closeBackFrame();
  }), $(".carousel").length && $(".carousel").carousel({
    interval: 3e3,
    ride: !0,
    pause: !1
  }), $(".btn-close").on("click", function () {
    closeBackFrame();
  }), $(".minus").click(function () {
    var e = $(this).parent().find("input"),
      a = parseInt(e.val()) - 1;
    return a = a < 0 ? 0 : a, e.val(a), e.change(), !1;
  }), $(".plus").click(function () {
    var e = $(this).parent().find("input");
    return e.val(parseInt(e.val()) + 1), e.change(), !1;
  }), Fancybox.bind('[data-fancybox="gallery"]', {
    hideScrollbar: !1
  }), $(".left-menu__item").on("click", function (e) {
    e.preventDefault(), $(this).hasClass("active") ? $(this).removeClass("active") : ($(".left-menu__item").removeClass("active"), $(this).addClass("active"));
  });
});